import React from "react";
import { Image, Box, Square, Button, IconButton } from "@chakra-ui/react";
import { StarIcon, CheckIcon, CloseIcon } from "@chakra-ui/icons";
import "./BoxClass.css";
// import { ClaseContext } from "../../contexts/ClasesContext";
import { useNavigate } from "react-router-dom";
import { MdArrowForward, MdEdit } from "react-icons/md";

export default function Caja({
  id,
  nombreClase,
  dias,
  rating,
  review,
  precio,
  duracion,
  cupos,
  inscriptos,
}) {
  // const { clase } = useContext(ClaseContext);
  const navigate = useNavigate();
  const redirect = () => {
    const path = "/comentarios/";
    navigate(path);
  };
  const property = {
    imagen:
      "data:image/jpeg;base64,",
    rating: { rating },
  };

  return (
    <Box
      m={5}
      maxW="sm"
      borderWidth="1px"
      borderRadius="lg"
      overflow="hidden"
      bg="white"
    >
      <Image class="imagen" src={property.imagen} alt={id} />
      <Box>
        <Square>
          <Box display="flex" alignItems="center">
            <Box
              color="gray.500"
              fontWeight="semibold"
              letterSpacing="wide"
              fontSize="xs"
              textTransform="uppercase"
              ml="2"
            >
              &bull; {cupos} cupos | &bull; {inscriptos} inscriptos
            </Box>
          </Box>
        </Square>

        <Box
          mt="1"
          fontWeight="semibold"
          as="h4"
          lineHeight="tight"
          noOfLines={1}
        >
          {nombreClase}
        </Box>

        <Box
          mt="1"
          fontWeight="semibold"
          as="h4"
          lineHeight="tight"
          noOfLines={1}
        >
          {dias}
        </Box>

        <Box>
          <Box as="span" color="gray.600" fontSize="sm">
            {precio} precio
          </Box>
        </Box>

        <Box>
          <Box as="span" color="gray.600" fontSize="sm">
            {duracion} duracion
          </Box>
        </Box>
        <Box ml="3" mt="2" alignItems="center">
          {Array(5)
            .fill("")
            .map((_, i) => (
              <StarIcon key={i} color={i < rating ? "blue" : "gray"} />
            ))}
          <Box as="span" ml="3" color="gray.600" fontSize="sm">
            {review} reviews
          </Box>
          <Box as="span" ml="10" color="gray.600" fontSize="sm">
            <IconButton
              m="1"
              variant="outline"
              colorScheme="blue"
              aria-label="Send email"
              icon={<MdEdit />}
            />
            <IconButton
              m="1"
              variant="outline"
              colorScheme="green"
              aria-label="Send email"
              icon={<CheckIcon />}
            />
            <IconButton
              m="1"
              variant="outline"
              colorScheme="red"
              aria-label="Send email"
              icon={<CloseIcon />}
            />
          </Box>
        </Box>
        <Box as="span" fontSize="sm">
          <Button
            mt="3"
            colorScheme="red"
            rightIcon={<MdArrowForward />}
            variant="outline"
            onClick={redirect}
          >
            Comentarios
          </Button>
        </Box>
      </Box>
    </Box>
  );
}
